//https://daveceddia.com/access-control-allow-origin-cors-errors-in-react-express/

import React from 'react';

class Main extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      imageURL: '',
    };

    this.handleUploadImage = this.handleUploadImage.bind(this);
    this.displayImage = this.displayImage.bind(this);
  }
  displayImage(ev){
    ev.preventDefault();
    if (ev.target.files && ev.target.files[0]) {
      this.setState({
        imageURL: URL.createObjectURL(ev.target.files[0])
      });
    }
  }

  handleUploadImage(ev) {
    ev.preventDefault();

    const data = new FormData();
    data.append('file', this.uploadInput.files[0]);

    fetch('http://192.168.225.26:3001/upload', {
      method: 'POST',
      body: data,
      headers:{
        'Access-Control-Allow-Origin':'*'
     },
    }).then((response) => {
      response.json().then((body) => {
        this.setState({ imageURL: `http://192.168.225.26:3001/${body.file}` });
      });
    });
  }

  render() {
    return (
      <form onSubmit={this.handleUploadImage} >
        <div>
          <input ref={(ref) => { this.uploadInput = ref; }} onChange={this.displayImage} type="file" />
        </div>
        <br />
        <div>
          <button>Upload</button>
        </div>
        <br />
            <img id="target" src={this.state.imageURL} alt="Image" className="uploaded_image"/>
      </form>
    );
  }
}

export default Main;
