import React from 'react';

class Main extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      imageURL: '',
    };

    this.handleUploadImage = this.handleUploadImage.bind(this);
    this.displayImage = this.displayImage.bind(this);
  }

  displayImage(ev){
    ev.preventDefault();
    if (ev.target.files && ev.target.files[0]) {
      this.setState({
        imageURL: URL.createObjectURL(ev.target.files[0])
      });
    }
  }

  handleUploadImage(ev) {
    ev.preventDefault();

    const data = new FormData();
    data.append('file', this.uploadInput.files[0]);

    fetch('http://192.168.225.26:3001/upload', {
      method: 'POST',
      body: data,
    }).then((response) => {
      response.json().then((body) => {
        alert(body.Data[29].result);
	window.location='display_result.html';
	//document.getElementById('table').innerHTML = display_result(body);
      });
    });
  }

  render() {
    return (
      <form onSubmit={this.handleUploadImage} >
        <div>
          <input ref={(ref) => { this.uploadInput = ref; }} onChange={this.displayImage} type="file" />
        </div>
        <br />
        <div>
          <button>Upload</button>
        </div>
        <br />
            <img id="target" src={this.state.imageURL} height="300vmin"  />
	    <br /><div id="table"></div>
     </form>
    );
  }

}
